import React, { useState } from 'react';
import GoogleMapReact from 'google-map-react';
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";


const LocalGameMap = ( { currPlayer } ) => {
  const [longitude, setLongitude] = React.useState(currPlayer ? currPlayer.longitude : 0);
  const [latitude, setLatitude] = React.useState(currPlayer ? currPlayer.latitude : 0);

  const center = {lat: latitude, lng: longitude};
  console.log("Loading");

  return (
  <div style={{height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: "AIzaSyCIXHbmHW8R8wNWLQNI1ZmWpmqqwptmW5o"}}
      defaultCenter={center}
      defaultZoom={11}
    ></GoogleMapReact>
  </div>
);


};

export default LocalGameMap;
