import React, { useState, useEffect } from "react";
import "./App.css";

import styled from "styled-components";
import { Container, Button } from "reactstrap";

import { GoogleLogin, GoogleLogout } from "react-google-login";

import PersonPage from "./components/PersonPage"

const Title = styled.h1`
  text-align: center;
  padding: 1rem;
  background-color: #6c757d;
  color: white;
`;
const ButtonBar = styled.div`
  padding: 0.5rem 0;
  margin-bottom: 0.5rem;
`;

const GOOGLE_CLIENT_ID = "891271447458-2tacoegsns3ccft1b88g46t3a0kkn6ep.apps.googleusercontent.com";

const App = () => {
  const [loggedIn, setLogin] = React.useState(false);
  const [currPlayer, setPlayer] = React.useState(null);
  const [mode, setMode] = React.useState("home");

  const handleGoogleLogin = (response) => {
  fetch('/login', {
    method: 'POST',
    headers: {
      Authorization: `Bearer ${response.tokenId}`,
    },
  }).then((fetchResponse) => {
    if (!fetchResponse.ok) {
      alert('Unable to authenticate', fetchResponse.statusText);
      setLogin(false);
    } else {
      setLogin(true);
      const customer = fetch(`/api/players/${response.googleId}/`)
        .then(response => {
          if (!response.ok) {
            throw new Error(response.status_text);
          }
          return response.json();
        })
        .then(data => {
          return data;
        })
        .catch(error => {
          console.log(error);
        });
      customer
        .then(data => {
          setPlayer(data[0]);
        }).catch(error => {
          console.log(error);
        });
    }
  });
};

  const handleGoogleFailure = response => {
    alert(response.error);
  };

  const handleGoogleLogout = () => {
    fetch("/logout", {
      method: "POST"
    }).then(fetchResponse => {
      if (!fetchResponse.ok) {
        alert("Error logging out", fetchResponse.statusText);
      }
      setLogin(false);
    });
  };

  const handleEditReturn = (player) => {





  };

  const loginButton = (
    <GoogleLogin
      clientId={GOOGLE_CLIENT_ID}
      buttonText="Login with Google"
      isSignedIn
      onSuccess={handleGoogleLogin}
      onFailure={handleGoogleFailure}
    />
  );
  const logoutButton = (
    <GoogleLogout
      clientId={GOOGLE_CLIENT_ID}
      buttonText="Logout"
      onLogoutSuccess={handleGoogleLogout}
    />
  );

  const profileButton = (
    <Button
      onClick={() => setMode("profile")}>
      Profile
      </Button>

  );

  if (mode === "home") {
    return (
      <div>
        <Title>Log-In Tests</Title>
        <ButtonBar>
          {!loggedIn && loginButton} {loggedIn && profileButton} {loggedIn && logoutButton}
        </ButtonBar>
      </div>

    );
  }

  return (
    <div>
      <Title>Profile Page</Title>
      <PersonPage backHome={setMode} currPlayer={currPlayer} ></PersonPage>
    </div>

  );


};

export default App;
