import React, { useState } from 'react';
import GoogleMapReact from 'google-map-react';
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";


const LocalGameMap = ( { currPlayer, setNewLatitude, setNewLongitude } ) => {
  const [longitude, setLongitude] = React.useState(currPlayer.longitude ? currPlayer.longitude : -73.940007);
  const [latitude, setLatitude] = React.useState(currPlayer.latitude ? currPlayer.latitude : 40.698413);

  console.log(longitude);
  console.log(latitude);
  const center = {lat: latitude, lng: longitude};


  if (setNewLatitude && setNewLongitude) {
    return (
      <div style={{height: '100vh', width: '100%' }}>
        <GoogleMapReact
          bootstrapURLKeys={{ key: "AIzaSyCIXHbmHW8R8wNWLQNI1ZmWpmqqwptmW5o"}}
          defaultCenter={center}
          defaultZoom={9}
          onClick={(obj) => {
            setNewLatitude(obj.lat);
            setNewLongitude(obj.lng);
            console.log(obj.lat, obj.lng);
          }}
        ></GoogleMapReact>
      </div>
    );

  }
  return (
  <div style={{height: '100vh', width: '100%' }}>
    <GoogleMapReact
      bootstrapURLKeys={{ key: "AIzaSyCIXHbmHW8R8wNWLQNI1ZmWpmqqwptmW5o"}}
      defaultCenter={center}
      defaultZoom={9}
    ></GoogleMapReact>
  </div>
);


};

export default LocalGameMap;
