import React, {Component, Fragment} from 'react';
import classes from './Layout.module.css';
import Toolbar from '../../components/Navigation/Toolbar/Toolbar';
import SideDrawer from '../../components/Navigation/SideDrawer/SideDrawer';

const Layout = ( { handleGoogleLogin, handleGoogleLogout, handleGoogleFailure, handleEditReturn, currPlayer, setMode, children } ) => {
    const [showSideDrawer, setShowSideDrawer] = React.useState(false);

    const sideDrawerClosedHandler = () => {
        setShowSideDrawer(false);
    };

    const sideDrawerToggleHandler = () => {
        if (showSideDrawer === false) {
          setShowSideDrawer(true);
        }
        else {
          setShowSideDrawer(false);
        }
    };
    return (
            <Fragment>
                <Toolbar drawerToggleClicked={sideDrawerToggleHandler}
                        handleGoogleLogin={handleGoogleLogin}
                        handleGoogleLogout={handleGoogleLogout}
                        handleGoogleFailure={handleGoogleFailure}
                        handleEditReturn={handleEditReturn}
                        setMode={setMode}
                        currPlayer={currPlayer}/>
                <SideDrawer
                    open={showSideDrawer}
                    closed={sideDrawerClosedHandler} />
                <main className={classes.Content}>
                    {children}
                </main>
            </Fragment>
    );

};

export default Layout;
