import React from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";

const Welcome = styled.h3`
  text-align: center;
  padding: 1rem;
  background-color: #fff000;
  color: red;
`;

const Statistic = styled.input`
  text-align: center;
`;

const PersonPage = ( { backHome, currPlayer } ) => {
  const [newheight, setNewHeight] = React.useState(currPlayer.height ? currPlayer.height : 0);
  const [newweight, setNewWeight] = React.useState(currPlayer.height ? currPlayer.height : 0);
  const [newbirthday, setNewBirthday] = React.useState(currPlayer.birthday ? currPlayer.birthday : "");
  const [newlocation, setNewLocation] = React.useState(currPlayer.primLocation ? currPlayer.primLocation : "");
  const [newposition, setNewPosition] = React.useState(currPlayer.position ? currPlayer.position : 0);


  return (
    <div>
      <Container>
        <Row>
          <Welcome>Welcome {currPlayer.name}</Welcome>
        </Row>
        <Row>
          <Col>
            <label>Height (in inches): </label>
          </Col>
          <Col>
            <Statistic value={newheight}></Statistic>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Weight (in pounds): </label>
          </Col>
          <Col>
            <Statistic value={newweight}></Statistic>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Birthday (MM/DD/YYYY): </label>
          </Col>
          <Col>
            <Statistic value={newbirthday}></Statistic>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Primary Location (City, State): </label>
          </Col>
          <Col>
            <Statistic value={newlocation}></Statistic>
          </Col>
        </Row>
        <Row>
          <Col>
            <label>Position (1- PG, 2- SG...): </label>
          </Col>
          <Col>
            <Statistic value={newposition}></Statistic>
          </Col>
        </Row>

      </Container>
    </div>


  );


}

export default PersonPage;
